import React from 'react';
import { useHistory } from 'react-router-dom';
import '../styles/styles.css';

const SelectShoppingAppPage: React.FC = () => {
    const history = useHistory();

    const handleSelect = () => {
        history.push('/feedback-submitted');
    };

    return (
        <div className="container">
            <h1 style={{ color: 'yellow' }}>Select Shopping App</h1>
            <p>Please select the shopping app you used to purchase the item:</p>
            <div className="shopping-apps">
                <div className="shopping-app">
                    <img src="data:image/jpeg;base64," alt="Lazada" />
                    <button style={{ backgroundColor: '#F37021', color: '#fff' }} onClick={handleSelect}>Select Lazada</button>
                </div>
                <div className="shopping-app">
                    <img src="data:image/png;base64," alt="Zalora" />
                    <button style={{ backgroundColor: '#000', color: '#fff' }} onClick={handleSelect}>Select Zalora</button>
                </div>
                <div className="shopping-app">
                    <img src="data:image/jpeg;base64," alt="Amazon" />
                    <button style={{ backgroundColor: '#FF9900', color: '#fff' }} onClick={handleSelect}>Select Amazon</button>
                </div>
                <div className="shopping-app">
                    <img src="data:image/png;base64," alt="Shopee" />
                    <button style={{ backgroundColor: '#FF5722', color: '#fff' }} onClick={handleSelect}>Select Shopee</button>
                </div>
                <div className="shopping-app">
                    <img src="data:image/jpeg;base64," alt="Temu" />
                    <button style={{ backgroundColor: '#FF6F00', color: '#fff' }} onClick={handleSelect}>Select Temu</button>
                </div>
                <div className="shopping-app">
                    <img src="data:image/png;base64," alt="Shein" />
                    <button style={{ backgroundColor: '#000', color: '#fff' }} onClick={handleSelect}>Select Shein</button>
                </div>
            </div>
        </div>
    );
};

export default SelectShoppingAppPage;